import { createStore } from "redux";
import {createSlice} from '@reduxjs/toolkit';

const initialState = { counter: 0, showCounter: true };

// pomoci createSlice delame slice naseho global state.
// kazdy slice musi mit svuj name, initialState a reducers (metody), ktere tento slice potrebuje.
// PRI POUZITI TOOLKITU UZ NEPOTREBUJEME IF CHECKS. VYLOZENE JEN PISEME METODY.
// NAVIC MUZEME MUTOVAT STATE!!!!!!!!! ALE NE PRIMO. napiseme napr. state.counter++, coz je vlastne spatne. Ale redux Toolkit ma dalsi
// package, pomoci ktereho si naclonuje stav a nedojde k mutovani stavu, nasledne prepise jen to, co my menime in immutable way. takhle se nam zjednodusi prace. nemusime pokazde opisovat stavovy objekt.
const counterSlice = createSlice({
    name: 'counter',
    initialState, // takhle je to pomoci moderniho JS. pomoci stareho zpusobu: initialState: initialState,
    reducers: {
        // vsechny reducers muzou mit v inputu state a action. u incremenetu a decrementu jsme ale action nepotrebovali.
        increment(state) {
            state.counter++;
        },
        decrement(state) {
            state.counter--;
        },
        increase(state,action) {
            state.counter = state.counter + action.value; // zde to nezapisujeme jako cast objektu (tzn. state.counter: state.counter+1), ale vylozene prepisujeme state.counter = ...
        },
        toggleCounter(state) {
            state.showCounter = !state.showCounter;
        },
    }
});



export default store;

// postupne ale tento soubor zacina nabobtnavat a je slozitejsi handlovat ty states. proto pouzivame redux Toolkit.
// instalace redux toolkitu: npm install @reduxjs/toolkit

/*
Takhle to bylo pomoci if checks a reducers, tzn. bez REDUX TOOLKIT.
const counterReducer = (state = initialState, action) => {
  // default state je InitialState

  // pomoci reduxu vzdycky overwritujeme stary stav nejakym novym stavem.
  if (action.type === "increment") {
    return {
      counter: state.counter + 1,
      showCounter: state.showCounter, // tohle nechame stejnee (tedy state). kdyz bychom to tady nenapsali,
      // tak by to bylo undefined, coz se bere jako false, takze by ten counter zmizel.
    };
  }

  if (action.type === "increase") {
    return {
      counter: state.counter + action.value,
      showCounter: state.showCounter,
    };
  }

  if (action.type === "decrement") {
    return {
      counter: state.counter - 1,
      showCounter: state.showCounter,
    };
  }

  if (action.type === "toggle") {
    return {
      counter: state.counter,
      showCounter: !state.showCounter,
    };
  }

  return state;
};

const store = createStore(counterReducer);
*/

