// import { createStore } from "redux"; // tohle jme pouzivali kdyz jsme nemeli toolkit. ted pouzivame configureStore.
import { createSlice, configureStore } from "@reduxjs/toolkit";

const initialCounterState = { counter: 0, showCounter: true };

// pomoci createSlice delame slice naseho global state.
// kazdy slice musi mit svuj name, initialState a reducers (metody), ktere tento slice potrebuje.
// PRI POUZITI TOOLKITU UZ NEPOTREBUJEME IF CHECKS. VYLOZENE JEN PISEME METODY.
// NAVIC MUZEME MUTOVAT STATE!!!!!!!!! ALE NE PRIMO. napiseme napr. state.counter++, coz je vlastne spatne. Ale redux Toolkit ma dalsi
// package, pomoci ktereho si naclonuje stav a nedojde k mutovani stavu, nasledne prepise jen to, co my menime in immutable way. takhle se nam zjednodusi prace. nemusime pokazde opisovat stavovy objekt.
const counterSlice = createSlice({
  name: "counter",
  initialState: initialCounterState, // takhle je to pomoci moderniho JS. pomoci stareho zpusobu: initialState: initialState,
  reducers: {
    // vsechny reducers muzou mit v inputu state a action. u incremenetu a decrementu jsme ale action nepotrebovali.
    increment(state) {
      state.counter++;
    },
    decrement(state) {
      state.counter--;
    },
    increase(state, action) {
      state.counter = state.counter + action.payload; // zde to nezapisujeme jako cast objektu (tzn. state.counter: state.counter+1), ale vylozene prepisujeme state.counter = ...
      // bereme payload (automaticky vytvorene v tom objektu). ne value.
    },
    toggleCounter(state) {
      state.showCounter = !state.showCounter;
    },
  },
});

// vytvorime novy slice pro authentication
const initialAuthState = {
  isAuthenticated: false,
};

const authSlice = createSlice({
  name: "authentication",
  initialState: initialAuthState,
  reducers: {
    login(state) {
      state.isAuthenticated = true;
    },
    logout(state) {
      state.isAuthenticated = false;
    },
  },
});

// takhle to jde zapsat: const store = createStore(counterSlice.reducer); // ten nas reducer je vlastne vice reduceru v jednom.
// ale problem by byl, kdybychom meli vice slicu. protoze do createStore jde zapsat jen jeden reducer.
// proto pouzijeme configureStore
// do configure objektu zapiseme reducer. opet to musi byt ve vysledku jen jeden reducer. ale muzeme to zapsat nasledovne, aby vznikla mapa reduceru.
const store = configureStore({
  reducer: { counter: counterSlice.reducer, auth: authSlice.reducer }, // ty reducery se pak mergnou do jednoho velkeho reduceru.
  // reducer: counterSlice.reducer, //kdyz jsme meli jen counter, tak to sl ozapsat takhle, protoze mame jen jeden slice.
});

// dispatching actions
//counterSlice.actions.toggleCounter() // it will create an action obejct with unique identifiers.
export const counterActions = counterSlice.actions;
export const authActions = authSlice.actions;

export default store;

//---------------------------------------------------------------------------------------------------------
// postupne tento soubor zacinal nabobtnavat a je slozitejsi handlovat ty states. proto pouzivame redux Toolkit.
// instalace redux toolkitu: npm install @reduxjs/toolkit

/*
Takhle to bylo pomoci if checks a reducers, tzn. bez REDUX TOOLKIT.
const counterReducer = (state = initialState, action) => {
  // default state je InitialState

  // pomoci reduxu vzdycky overwritujeme stary stav nejakym novym stavem.
  if (action.type === "increment") {
    return {
      counter: state.counter + 1,
      showCounter: state.showCounter, // tohle nechame stejnee (tedy state). kdyz bychom to tady nenapsali,
      // tak by to bylo undefined, coz se bere jako false, takze by ten counter zmizel.
    };
  }

  if (action.type === "increase") {
    return {
      counter: state.counter + action.value,
      showCounter: state.showCounter,
    };
  }

  if (action.type === "decrement") {
    return {
      counter: state.counter - 1,
      showCounter: state.showCounter,
    };
  }

  if (action.type === "toggle") {
    return {
      counter: state.counter,
      showCounter: !state.showCounter,
    };
  }

  return state;
};

const store = createStore(counterReducer);
*/

